import { useSelector } from "react-redux";
import CartItem from "../components/CartItem";

const Cart = () => {

  const {cart} = useSelector((state) => state);



  return (
    <div>
      {
        cart.length >0 ? 
        (
          <div>

            <div>
              {
                cart.map((item,index) => {
                  return <CartItem key={item.id} item = {item} itemIndex={index}  />
                })
              }
            </div>

            <div>

               <div>
                   <div>Your Cart </div>
                   <div>Summary </div>
                   <p>
                    <span>Total Items: {cart.length}  </span>
                   </p>
               </div>

               <div>
                <p>Total Am</p>
               </div>

            </div>


          </div>
        ) :
        (<div>
          <h1> Cart Empty </h1>
           <Link to ={"/"}>
             <button>
               Shop Now 
             </button>
           </Link>
          </div>
        )
      }
    </div>
  );
};

export default Cart;
